import { StyleSheet, TextInput, View, SafeAreaView, Text, Alert, TouchableOpacity } from 'react-native'
import React, {useState, useEffect} from 'react'
import DatabaseConnection from "../../database/database-connection";

const db = DatabaseConnection.getConnection();

const AddReplacement = ({ navigation }) => {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState(0);

  //función para hacer el submit desde el botón
  const submitSupply = () =>{
          // guardar los datos

          db.transaction((tx) => {

            tx.executeSql(
              `INSERT INTO supplies (name, amount) VALUES (?, ?)`,
              [name, amount],
              (tx, results) => {
                console.log("results", results);
                // validar resultado
                if (results.rowsAffected > 0) {
                              
                  Alert.alert(
                    "Exito",
                    "Insumo registrado.",
                    [
                      {
                        text: "Ok",
                      },
                    ],
                    { cancelable: false }
                  );
                } else {
                  Alert.alert("Error al registrar insumo");
                }
              }
            );
          });

  }

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.viewContainer}>
          <TextInput style={styles.textInput} onChangeText={setName} editable maxLength={50} placeholder="Nombre" />
          <TextInput style={styles.textInput} onChangeText={setAmount} editable maxLength={10} placeholder="Cantidad" />
          <TouchableOpacity style={[styles.submitButton]} onPress={submitSupply}>
            <Text style={styles.text}>Agregar</Text>
          </TouchableOpacity>
        
      </View>
    </SafeAreaView>
  )
}

export default AddSupply

const styles = StyleSheet.create({
  container: {

  },
  viewContainer: {
    padding: 30
  },
  textInput: {
    borderColor: "#142492",
    borderWidth: 4,
    borderRadius: 5,
    width: 300,
    padding: 7,
    color: "#142492",
    marginLeft: 'auto',
    marginRight: 'auto',
    marginBottom: 30
  },
  submitButton: {
    marginLeft: 'auto',
    marginRight: 'auto',
    width: 200,
    backgroundColor: 'green',
    textAlign: 'center',
    padding: 10,
    borderRadius: 5,
    
  },
  text: {
    color: 'white',
    marginLeft: 'auto',
    marginRight: 'auto',
    width: 55,
    fontSize: 15
  }
})
