import { StyleSheet, TextInput, View, SafeAreaView, Text, Alert, TouchableOpacity } from 'react-native'
import React from 'react'

const submitUser = () =>{

  Alert.alert(  
    'Alert Title',  
    'My Alert Msg',  
    [  
        {  
            text: 'Cancel',  
            onPress: () => console.log('Cancel Pressed'),  
            style: 'cancel',  
        },  
        {text: 'OK', onPress: () => console.log('OK Pressed')},  
    ]  
);  
}

const AddUser = () => {

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.viewContainer}>
        <form>
          <TextInput style={styles.textInput} id="inputName" editable maxLength={40} placeholder="Nombre" />
          <TextInput style={styles.textInput} id="inputLastName" editable maxLength={40} placeholder="Apellido" />
          <TextInput style={styles.textInput} id="inputCI" editable maxLength={8} placeholder="C.I" />
          <TextInput style={styles.textInput} id="inputMatAuto" editable maxLength={7} placeholder="MAT. Auto" />
          <TouchableOpacity style={[styles.submitButton]} onPress={submitUser}>
            <Text style={styles.text}>Agregar</Text>
          </TouchableOpacity>
        </form>
      </View>
    </SafeAreaView>
  )
}

export default AddUser

const styles = StyleSheet.create({
  container: {

  },
  viewContainer: {
    padding: 30
  },
  textInput: {
    borderColor: "#142492",
    borderWidth: 4,
    borderRadius: 5,
    width: 300,
    padding: 7,
    color: "#142492",
    marginLeft: 'auto',
    marginRight: 'auto',
    marginBottom: 30
  },
  submitButton: {
    marginLeft: 'auto',
    marginRight: 'auto',
    width: 200,
    backgroundColor: 'green',
    textAlign: 'center',
    padding: 10,
    borderRadius: 5,
    
  },
  text: {
    color: 'white',
  }
})
