import { StyleSheet, Text, View, SafeAreaView, Alert, FlatList } from 'react-native'
import React, {useEffect} from 'react'
import MyText from "../../components/MyText";
import { useFocusEffect } from '@react-navigation/native';
import DatabaseConnection from "../../database/database-connection";

const db = DatabaseConnection.getConnection();


const DetailedList = () => {
  const [treatments, setTreatments] = React.useState([]);
  useEffect(() => {

    db.transaction( (txn) => {

      txn.executeSql(`SELECT * FROM treatments INNER JOIN cars ON treatments.carCode = cars.carCode`, [], (tx, results) => {
        // validar resultado
        
        if (results.rows.length > 0) {
          var temp = [];
          for (let i = 0; i < results.rows.length; ++i){

            txn.executeSql(`SELECT name, lastname FROM users INNER JOIN cars ON cars.carCode = users.carCode WHERE cars.carCode= ?`, [results.rows.item(i).carCode], (tx, results2) => {

              var treatment = results.rows.item(i);
              treatment.uName = results2.rows.item(0).name
              treatment.uLastname = results2.rows.item(0).lastname

              txn.executeSql(`SELECT replacements.treatmentId FROM replacements inner join treatments on replacements.treatmentId = treatments.id`, [], (tx, results3) => {


                if(results3.rows.length!=0){
                  treatment.replacements = true;
                }
                else{
                  treatment.replacements = false;
                }
                temp.push(treatment);
              })
            })
            
          }
          setTreatments(temp);
        } else {
          Alert.alert(
            "Mensaje",
            "No hay tratamientos",
            [
              {
                text: "Ok",
              },
            ],
            { cancelable: false }
          );
        }
      }, []);

      /*db.transaction((tx) => {
        tx.executeSql(
          `DELETE FROM treatments WHERE name = ?`,
          [""],
          (tx, results) => {
            console.log("results", results);
            // validar resultado
            if (results.rowsAffected > 0) {
              Alert.alert("Tratamiento eliminado");
            } else {
              Alert.alert("El tratamiento no existe");
            }
          }
        );
      });*/
    });
  });
  const listItemView = (item) => {
    
    console.log("aaa")
    return (
      <View key={item.id} style={styles.listItemView}>
        <MyText text="Nombre tratamiento" style={styles.textL}/>
        <MyText text={item.name} style={styles.textL}/>

        <MyText text="Nombre cliente" style={styles.textL}/>
        <MyText text={item.uName + " "+ item.uLastname} style={styles.textL}/>

        <MyText text="Fecha inicio" style={styles.textL}/>
        <MyText text={item.fInicio} style={styles.textL}/>

        <MyText text="Fecha fin:" style={styles.textL}/>
        <MyText text={item.fFin} style={styles.textL}/>

        <MyText text="UsÃ³ repuestos:" style={styles.textL}/>
        <MyText text={item.replacements} style={styles.textL}/>

        <MyText text="Costo tratamiento:" style={styles.textL}/>
        <MyText text={item.costoTratamiento} style={styles.textL}/>

      </View>
    );
  };
  return (
    <SafeAreaView style={styles.container}>
    <View>
      <View>
          <View style={styles.view}>
            <FlatList 
            contentContainerStyle={{ paddingHorizontal: 20, paddingBottom: 150 }}
            data={treatments}
            keyExtractor={(index) => index.toString()}
            renderItem={({ item }) => listItemView(item)}

            />
          </View>
      </View>
    </View>
    </SafeAreaView>
  )
}

export default DetailedList

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  viewContainer: {
    flex: 1,
    backgroundColor: "white",
  },
  generalView: {
    flex: 1,
  },
  listView: {
    marginTop: 20,
  },
  listItemView: {
    backgroundColor: "white",
    margin: 5,
    padding: 5,
    borderRadius: 10,
  },
  textL: {
    padding: 5,
    marginLeft: 10,
    color: "black",
    alignContent: "center",
    alignItems: "center",
  },
  flatList: {
    backgroundColor: 'blue'
  },
  delBut: {
    marginTop: 20,
    
  }
})
