import { StyleSheet, TextInput, View, SafeAreaView, Text, Alert, TouchableOpacity } from 'react-native'
import React from 'react'

const submitUser = () =>{

  Alert.alert(  
    'Resultado de la operación',  
    '¡Usuario '+{}+' agregado con éxito!'
);  
}

const AddUser = () => {

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.viewContainer}>
        
          <TextInput style={styles.textInput} id="inputName" editable maxLength={40} placeholder="Nombre" />
          <TextInput style={styles.textInput} id="inputLastName" editable maxLength={40} placeholder="Apellido" />
          <TextInput style={styles.textInput} id="inputCI" editable maxLength={8} placeholder="C.I" />
          <TextInput style={styles.textInput} id="inputMatAuto" editable maxLength={7} placeholder="MAT. Auto" />
          <TouchableOpacity style={[styles.submitButton]} onPress={submitUser}>
            <Text style={styles.text}>Agregar</Text>
          </TouchableOpacity>
        
      </View>
    </SafeAreaView>
  )
}

export default AddUser

const styles = StyleSheet.create({
  container: {

  },
  viewContainer: {
    padding: 30
  },
  textInput: {
    borderColor: "#142492",
    borderWidth: 4,
    borderRadius: 5,
    width: 300,
    padding: 7,
    color: "#142492",
    marginLeft: 'auto',
    marginRight: 'auto',
    marginBottom: 30
  },
  submitButton: {
    marginLeft: 'auto',
    marginRight: 'auto',
    width: 200,
    backgroundColor: 'green',
    textAlign: 'center',
    padding: 10,
    borderRadius: 5,
    
  },
  text: {
    color: 'white',
    marginLeft: 'auto',
    marginRight: 'auto',
    width: 55,
    fontSize: 15
  }
})
